function fadeFromLeft(){document.querySelectorAll(".fade-from-left").forEach(e=>{e.getBoundingClientRect().top<window.innerHeight/1.1?e.classList.add("fade-start-left"):e.classList.remove("fade-start-left")})}function fadeFromRight(){document.querySelectorAll(".fade-from-right").forEach(e=>{e.getBoundingClientRect().top<window.innerHeight/1.1?e.classList.add("fade-start-right"):e.classList.remove("fade-start-right")})}function fadeUp(){document.querySelectorAll(".fade-up").forEach(e=>{e.getBoundingClientRect().top<window.innerHeight/1.1?e.classList.add("fade-up-start"):e.classList.remove("fade-up-start")})}window.addEventListener("scroll",fadeFromLeft),window.addEventListener("scroll",fadeFromRight),window.addEventListener("scroll",fadeUp);let headings=document.querySelectorAll(".fade-down");headings.forEach(e=>{e.getBoundingClientRect().top,window.innerHeight;window.onload=function(){e.classList.add("fade-down-start"),document.querySelector(".fade-in").classList.add("fade-in-start"),document.querySelector(".fade-up-now").classList.add("fade-up-now-start")}});